unit_test.jest_template:
describe('{FunctionName}', () => {
  it('should return expected result for valid input', () => {
    const result = {functionName}({validInput});
    expect(result).toEqual({expectedOutput});
  });

  it('should throw error for invalid input', () => {
    expect(() => {functionName}({invalidInput})).toThrow();
  });

  it('should handle edge case', () => {
    const result = {functionName}({edgeCaseInput});
    expect(result).toEqual({edgeCaseOutput});
  });
});

unit_test.pytest_template:
class Test{ClassName}:
    def test_valid_input(self):
        result = {function_name}({valid_input})
        assert result == {expected_output}

    def test_invalid_input(self):
        with pytest.raises(ValueError):
            {function_name}({invalid_input})

    def test_edge_case(self):
        result = {function_name}({edge_case})
        assert result == {edge_output}

integration_test.api_test_template:
describe('POST /api/users', () => {
  it('should create a new user', async () => {
    const res = await request(app)
      .post('/api/users')
      .send({ email: 'test@test.com', password: 'Pass123!' })
      .expect(201);
    expect(res.body.data).toHaveProperty('id');
  });

  it('should return 400 for invalid email', async () => {
    const res = await request(app)
      .post('/api/users')
      .send({ email: 'invalid', password: 'Pass123!' })
      .expect(400);
  });
});

component_test.react_template:
describe('{ComponentName}', () => {
  it('renders correctly', () => {
    render(<{ComponentName} />);
    expect(screen.getByText('{expectedText}')).toBeInTheDocument();
  });

  it('handles click event', () => {
    const onClick = jest.fn();
    render(<{ComponentName} onClick={onClick} />);
    fireEvent.click(screen.getByRole('button'));
    expect(onClick).toHaveBeenCalledTimes(1);
  });
});
